import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500">
      <nav className="absolute top-0 right-0 mt-4 mr-4 width-100%">
        <div className="title">
          <img src="#" alt="image" />
          <h1 className="text-4xl font-bold">ChatApp</h1>
        </div>
        <Link to="/login" className="text-white hover:text-gray-300 mx-2">
          Login
        </Link>
        <Link to="/signup" className="text-white hover:text-gray-300 mx-2">
          Signup
        </Link>
      </nav>
      <div className="text-white text-center">
        <h1 className="text-4xl font-bold mb-4">Welcome to ChatApp!</h1>
        <p className="text-lg mb-8">
          Connect with friends and start chatting in real-time. Share your thoughts, photos, and more!
        </p>
        <button
          className="bg-white text-indigo-600 font-bold py-2 px-4 rounded-full hover:bg-indigo-500 hover:text-white focus:outline-none focus:shadow-outline"
          type="button"
        >
          Get Started
        </button>
      </div>
    </div>
  );
}

export default HomePage;
